## íì˜ êµ¬í˜„::implementation

::code-group
```cpp::c++
using namespace std;

template <typename T>
class Queue {
private:
    struct Node {
        T data;
        Node *next;
        explicit Node(const T &data): data(data), next(nullptr) {}
    };  
    Node *front;
    Node *rear;
    size_t size;

public:
    Queue(): front(nullptr), rear(nullptr), size(0) {}
    ~Queue() {
        while (!is_empty()) {
            dequeue();
        }
    }
    bool is_empty() const {
        return size == 0;
    }
    void enqueue(T data) {
        Node *newNode = new Node(data);
        if (is_empty()) {
            front = newNode;
        } else {
            rear->next = newNode;
        }
        rear = newNode;
        size++;
    }
    T dequeue() {
        if (is_empty()) throw underflow_error("Queue is empty!");
        Node* temp = front;
        front = front->next;
        T data = temp->data;
        delete temp;

        if (front == nullptr) rear = nullptr;

        size--;
        return data;
    }
    T peek() const {
        if (is_empty()) throw underflow_error("Queue is empty!");
        return front->data;
    }
    friend ostream& operator<<(ostream& os, const Queue& q) {
        os << "[";
        Node* temp = q.front;
        while (temp != nullptr) {
            os << temp->data;
            if (temp->next != nullptr) os << ", ";
            temp = temp->next;
        }

        os << "]";
        return os;
    }
};
```
```java::java
public class Queue<T> {
    private class Node {
        T data;
        Node next;
        Node(T data) {
            this.data = data;
            this.next = null;
        }
    }
    private Node front;
    private Node rear;
    private int size;

    public Queue() {
        front = null;
        rear = null;
        size = 0;
    }

    public boolean isEmpty() {
        return size == 0;
    }

    public void enqueue(T data) {
        Node newNode = new Node(data);
        if (isEmpty()) {
            front = newNode;
        } else {
            rear.next = newNode;
        }
        rear = newNode;
        size++;
    }

    public T dequeue() {
        if (isEmpty()) throw new RuntimeException("Queue is empty!");
        Node temp = front;
        front = front.next;
        T data = temp.data;
        temp = null;

        if (front == null) rear = null;

        size--;
        return data;
    }

    public T peek() {
        if (isEmpty()) throw new RuntimeException("Queue is empty!");
        return front.data;
    }

    @Override
    public String toString() {
        StringBuilder sb = new StringBuilder();
        sb.append("[");
        Node temp = front;
        while (temp != null) {
            sb.append(temp.data);
            if (temp.next != null) sb.append(", ");
            temp = temp.next;
        }
        sb.append("]");
        return sb.toString();
    }
}    
```
