#include <iostream>
#include "Stack.h"
using namespace std;

template <typename T>
class ArrayStack final : public Stack<T> {
private:
    T* arr;
    int top;
    int cap;
public:
    explicit ArrayStack(const int cap): top(-1), cap(cap) {
        arr = new T[cap];
    }

    ~ArrayStack() override {
        delete[] arr;
    }

    void push(const T& item) override {
        if (top == cap -1) {
            throw overflow_error("Stack is full");
        }
        arr[++top] = item;
    }

    T pop() override {
        if (is_empty()) {
            throw underflow_error("Stack is empty");
        }

        return arr[top--];
    }

    T peek() override {
        if (is_empty()) {
            throw underflow_error("Stack is empty");
        }

        return arr[top];
    }

    bool is_empty() override {
        return top == -1;
    }

    int size() override {
        return top + 1;
    }

    friend ostream& operator<<(ostream& os, const ArrayStack<T>& stack) {
        os << "Stack: [";
        for (int i = stack.top; i >= 0; i--) {
            os << stack.arr[i];
            if (0 < i) os << ",";
        }
        os << "]";
        return os;
    }
};
