public class LinkedListStack implements Stack {
    private Node top;
    private int cap;

    public LinkedListStack(int capacity) {
        this.cap = capacity;
        this.top = null;
    }

    @Override
    public void push(Object item) {
        Node newNode = new Node(item);
        newNode.next = top;
        top = newNode;
        cap++;
    }

    @Override
    public Object pop() {
        if (isEmpty()) {
            throw new StackUnderflowError("Stack is empty");
        }
        Node temp = top;
        Object popped = temp.data;
        top = top.next;
        temp.next = null;
        cap--;
        return popped;
    }

    @Override
    public Object peek() {
        if (isEmpty()) {
            throw new StackUnderflowError("Stack is empty");
        }
        return top.data;
    }

    @Override
    public boolean isEmpty() {
        return top == null;
    }

    @Override
    public int size() {
        return top == null ? 0 : cap;
    }

    @Override
    public String toString() {
        StringBuilder sb = new StringBuilder("Stack: [");
        Node current = top;
        while (current != null) {
            sb.append(current.data);
            if (current.next != null) {
                sb.append(", ");
            }
            current = current.next;
        }
        sb.append("]");
        
        return sb.toString();
    }
    
    public static class Node<T> {
        T data;
        Node next;
        
        public Node(T data) {
            this.data = data;
            this.next = null;
        }
    }
