import java.util.Arrays;

public class Heap {

    //== í™(Heap) ì„ ë§Œë“œëŠ” ë©”ì†Œë“œ ==//
    public static void makeHeap(int arr[]) {
        // last heap
        int lh = arr.length / 2;
        System.out.println("Normal Array = " + Arrays.toString(arr));
        int eh = arr.length;
        while (lh-- > 0) {

            System.out.println("i: " + lh);
            pushDown(arr, lh, eh);
        }

        System.out.println("Array as Max Heap = " + Arrays.toString(arr));
    }


    //== ë…¸ë“œì™€ ê·¸ ìì‹ì¤‘ì—ì„œ ë”í°(ë˜ëŠ” ì‘ì€) ìœ„ì¹˜ë¥¼ ì°¾ëŠ” ë©”ì†Œë“œ ==//
    public static int findLargest(int arr[], int node, int eh) {
        // first child
        int fc = (2 * (node + 1)) - 1;

        if (fc + 1 < eh) {
            if (arr[fc] <= arr[fc + 1]) {
                return arr[fc + 1] <= arr[node] ? node : fc + 1;
            } else {
                return arr[fc] <= arr[node] ? node : fc;
            }
        }
        if (fc < eh && arr[node] < arr[fc]) {
            return fc;
        } else {
            return node;
        }
    }
    //== ê°’ì„ ì•„ë˜ë¡œ ë‚´ë¦¬ëŠ” ë©”ì†Œë“œ ==//

    /**
     * ê°„ë‹¨í•˜ê²Œ ë³´ë©´ íŠ¸ë¦¬ì—ì„œ ë§ˆì§€ë§‰ í™ì˜ ìœ„ì¹˜ë¥¼ êµ¬í•˜ê³ ,
     * ë£¨íŠ¸ ë…¸ë“œê¹Œì§€ ì—­ìˆœìœ¼ë¡œ í™ì„ ë§Œë“¤ì–´ê°‘ë‹ˆë‹¤.
     * 
     * ìµœëŒ€ í™(ë˜ëŠ” ìµœì†Œ í™)ì˜ ì¡°ê±´ì— ë”°ë¼ ë”í°(ë˜ëŠ” ë” ì‘ì€)ê°’ì„ ê¸°ì¤€ìœ¼ë¡œ,
     * í•´ë‹¹ ë…¸ë“œë¥¼ ìœ„ë¡œì˜¬ë¦¬ê³  ë³€ê²½ëœ ëŒ€ìƒì€ ì•„ë˜ë¡œ ë‚´ë ¤ê°€ë©° ê³„ì† ë°”ê¿‰ë‹ˆë‹¤.
     */
    public static void pushDown(int arr[], int node, int eh) {
        do {
            System.out.println("j: " + node);
            int temp = arr[node];
            int large = findLargest(arr, node, eh);
            System.out.println(drawBinaryTree(arr));

            if (large == node)
                break;

            arr[node] = arr[large];
            arr[large] = temp;

            node = large;
        } while (node <= eh);
    }

    public static void sort(int arr[]) {
        int last = arr.length;
        makeHeap(arr);

        while (--last >= 0) {

            int temp = arr[0];
            arr[0] = arr[last];
            arr[last] = temp;

            System.out.println("last: " + last);
            pushDown(arr, 0, last);
        }
        ;
    }

    //== ì¶œë ¥ ë©”ì†Œë“œ ==//
    public static String drawBinaryTree(int arr[]) {
        StringBuilder builder = new StringBuilder();

        int nol = (int) (Math.log(arr.length) / Math.log(2)) + 1;
        int max = (int) Math.pow(2, nol - 1);

        int printed = 0;
        for (int i = 0; i < nol; i++) {
            int perFloor = (int) Math.pow(2, i);
            int tab = (max - perFloor) / 2 + (max - perFloor) % 2;
            int last = printed + perFloor;

            for (int j = 0; j < tab; j++) {
                builder.append("  ");
            }
            for (int j = printed; (j < arr.length && j < last); j++) {
                builder.append(String.format("(%d)", arr[j]));
            }
            builder.append("\n");
            printed += perFloor;
        }

        return builder.toString();
    }

    public static void main(String args []) throws Exception {
        //== í™ ìœ¼ë¡œ ë§Œë“¤ê¸°==//
        int arrForMake [] = {1, 5, 8, 2, 74, 9, 12, 104, 87, 43};
        Heap.makeHeap();

        //== í™ + ì •ë ¬ ==//
        int arrForSort [] = {1, 8, 9, 15, 4, 7, 12, 6, 4, 17};
        Heap.sort(arr);

    }
}
